mapper.select:
SELECT DISTINCT p.profession_id,p.course_type_id,p.name,p.sort
FROM profession p
         JOIN course_to_profession ctp ON p.profession_id = ctp.profession_id
         JOIN user_to_course utc ON ctp.course_id = utc.course_id
WHERE utc.user_id = #{userId}
  AND p.course_type_id = #{courseTypeId}

SELECT DISTINCT p.profession_id,p.course_type_id,p.name,p.sort
FROM profession p
         JOIN course_to_profession ctp ON p.profession_id = ctp.profession_id
         JOIN course c ON c.course_id = ctp.course_id
WHERE c.manager_id = #{userId}
  AND p.course_type_id = #{courseTypeId}
